import React, { useState, useEffect } from 'react';

//Componente => bloco isolado de HTML, CSS e JS, o qual não interfere no restante da explicação
//Estado => informação que o componente vai mudar
//Propriedade => informações que um componente(Parent) passa para os componentes(Children)

const key = "KEY1_KEY1_KFJAKFJAL"

function App(){
  const [state, setState] = useState(0)

  useEffect(() => {
    const getItem = Math.floor(localStorage.getItem(key))
    console.log(getItem)
    if(getItem) setState(getItem)
  }, [])

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(state))
  }, [state])

  function handleClick() {
    setState(state + 1)
  }

  return (
    <>
    <h1>Contador: {state}</h1>
    <button onClick={handleClick}>
      Incrementar
    </button>
    </>
  )
}

export default App;
